mapper.select:
SELECT head.HEADER_ID, head.ORDER_NUMBER, head.COMPANY_ID, com.COMPANY_NAME, head.ORDER_DATE, head.ORDER_STATUS, head.CUSTOMER_ID, cus.CUSTOMER_NAME, line.ORDER_AMOUNT FROM hap_om_order_headers head LEFT JOIN hap_org_companys com ON head.COMPANY_ID = com.COMPANY_ID LEFT JOIN hap_ar_customers cus ON head.CUSTOMER_ID = cus.CUSTOMER_ID LEFT JOIN ( SELECT HEADER_ID, sum(ORDERD_QUANTITY*UNIT_SELLING_PRICE) as ORDER_AMOUNT FROM hap_om_order_lines GROUP BY HEADER_ID ) line ON head.HEADER_ID = line.HEADER_ID
AND head.HEADER_ID = #{headerId}
AND head.COMPANY_ID = #{companyId}
AND head.CUSTOMER_ID = #{customerId}
AND head.ORDER_NUMBER = #{orderNumber}
AND head.ORDER_STATUS = #{orderStatus}
AND head.HEADER_ID IN ( SELECT HEADER_ID FROM hap_om_order_lines WHERE INVENTORY_ITEM_ID = #{inventoryItemId} )
ORDER BY head.HEADER_ID

SELECT head.HEADER_ID, head.ORDER_NUMBER, head.COMPANY_ID, com.COMPANY_NAME, head.ORDER_DATE, head.ORDER_STATUS, item.ITEM_CODE, item.ITEM_DESCRIPTION, line.ORDERD_QUANTITY, line.UNIT_SELLING_PRICE, head.CUSTOMER_ID, cus.CUSTOMER_NAME, line.ORDERD_QUANTITY*line.UNIT_SELLING_PRICE as ORDER_AMOUNT FROM hap_om_order_headers head LEFT JOIN hap_om_order_lines line ON head.HEADER_ID = line.HEADER_ID LEFT JOIN hap_org_companys com ON head.COMPANY_ID = com.COMPANY_ID LEFT JOIN hap_ar_customers cus ON head.CUSTOMER_ID = cus.CUSTOMER_ID LEFT JOIN hap_inv_inventory_items item ON line.INVENTORY_ITEM_ID = item.INVENTORY_ITEM_ID
AND head.HEADER_ID = #{headerId}
AND head.COMPANY_ID = #{companyId}
AND head.CUSTOMER_ID = #{customerId}
AND head.ORDER_NUMBER = #{orderNumber}
AND head.ORDER_STATUS = #{orderStatus}
AND head.HEADER_ID IN ( SELECT HEADER_ID FROM hap_om_order_lines WHERE INVENTORY_ITEM_ID = #{inventoryItemId} )
